	/**
	 * Sets contents of passed object to values from current object.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      object $copyObj An object of Transaction (or compatible) type.
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @throws     PropelException
	 */
	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setCashId($this->cash_id);

		$copyObj->setUserId($this->user_id);

		$copyObj->setTransactionTypeId($this->transaction_type_id);

		$copyObj->setType($this->type);

		$copyObj->setAmount($this->amount);

		$copyObj->setObservation($this->observation);

		$copyObj->setCreatedAt($this->created_at);


		if ($deepCopy) {
			// important: temporarily setNew(false) because this affects the behavior of
			// the getter/setter methods for fkey referrer objects.
			$copyObj->setNew(false);

			$relObj = $this->getAccountTransaction();
			if ($relObj) {
				$copyObj->setAccountTransaction($relObj->copy($deepCopy));
			}

			$relObj = $this->getCreditTransaction();
			if ($relObj) {
				$copyObj->setCreditTransaction($relObj->copy($deepCopy));
			}

			$relObj = $this->getInvestmentTransaction();
			if ($relObj) {
				$copyObj->setInvestmentTransaction($relObj->copy($deepCopy));
			}

		} // if ($deepCopy)


		$copyObj->setNew(true);

		$copyObj->setId(NULL); // this is a auto-increment column, so set to default value

	}

	/**
	 * Makes a copy of this object that will be inserted as a new row in table when saved.
	 * It creates a new object filling in the simple attributes, but skipping any primary
	 * keys that are defined for the table.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @return     Transaction Clone of current object.
	 * @throws     PropelException
	 */
	public function copy($deepCopy = false)
	{
		// we use get_class(), because this might be a subclass
		$clazz = get_class($this);
		$copyObj = new $clazz();
		$this->copyInto($copyObj, $deepCopy);
		return $copyObj;
	}

	/**
	 * Returns a peer instance associated with this om.
	 *
	 * Since Peer classes are not to have any instance attributes, this method returns the
	 * same instance for all member of this class. The method could therefore
	 * be static, but this would prevent one from overriding the behavior.
	 *
	 * @return     TransactionPeer
	 */
	public function getPeer()
	{
		if (self::$peer === null) {
			self::$peer = new TransactionPeer();
		}
		return self::$peer;
	}

	/**
	 * Declares an association between this object and a Cash object.
	 *
	 * @param      Cash $v
	 * @return     Transaction The current object (for fluent API support)
	 * @throws     PropelException
	 */
	public function setCash(Cash $v = null)
	{
		if ($v === null) {
			$this->setCashId(NULL);
		} else {
			$this->setCashId($v->getId());
		}

		$this->aCash = $v;

		// Add binding for other direction of this n:n relationship.
		// If this object has already been added to the Cash object, it will not be re-added.
		if ($v !== null) {
			$v->addTransaction($this);
		}

		return $this;
	}


	/**
	 * Get the associated Cash object
	 *
	 * @param      PropelPDO Optional Connection object.
	 * @return     Cash The associated Cash object.
	 * @throws     PropelException
	 */
	public function getCash(PropelPDO $con = null)
	{
		if ($this->aCash === null && (($this->cash_id !== "" && $this->cash_id !== null))) {
			$this->aCash = CashPeer::retrieveByPk($this->cash_id);
			/* The following can be used additionally to
			   guarantee the related object contains a reference
			   to this object.  This level of coupling may, however, be
			   undesirable since it could result in an only partially populated collection
			   in the referenced object.
			   $this->aCash->addTransactions($this);
			 */
		}
		return $this->aCash;
	}

