	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->singleAccountTransaction) {
				$this->singleAccountTransaction->clearAllReferences($deep);
			}
			if ($this->singleCreditTransaction) {
				$this->singleCreditTransaction->clearAllReferences($deep);
			}
			if ($this->singleInvestmentTransaction) {
				$this->singleInvestmentTransaction->clearAllReferences($deep);
			}
		} // if ($deep)

		$this->singleAccountTransaction = null;
		$this->singleCreditTransaction = null;
		$this->singleInvestmentTransaction = null;
			$this->aCash = null;
			$this->aUser = null;
			$this->aTransactionType = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseTransaction:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseTransaction::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

